import Vue from "vue";
import VueRouter from "vue-router";
import CustomerPage from "@/components/CustomerPage.vue";  // 客戶頁面
import TransactionPage from "@/components/TransactionPage.vue"; // 交易頁面

Vue.use(VueRouter);

const routes = [
  { path: "/", redirect: "/customers" },  // 設置預設路由為顯示 CustomerPage
  { path: "/customers", component: CustomerPage },
  { path: "/transactions", component: TransactionPage },
];

const router = new VueRouter({
  mode: "history",  // 使用 history 模式，去掉 URL 中的 # 號
  routes,
});

export default router;
