import Vue from "vue";
import VueRouter from "vue-router";
import CustomerPage from "@/components/CustomerPage.vue";
import TransactionPage from "@/components/TransactionPage.vue";

Vue.use(VueRouter);

const routes = [
  { path: "/", redirect: "/customers" }, // 設定默認頁面
  { path: "/customers", component: CustomerPage }, // 客戶頁面
  { path: "/transactions", component: TransactionPage }, // 交易頁面
];

const router = new VueRouter({
  mode: "history", // 使用 history 模式去掉 # 號
  routes,
});

export default router;
